import { useForm } from "react-hook-form";
import { TextInput } from "../inputs_components/text-input/text-input";
import { useContext, useEffect } from "react";
import { FeedbackContext } from "../../context/feedback-context";

export const FormFeedback = (props) => {
  // const { feedbackForm, setFeedbackForm } = useContext(FeedbackContext);

  const defaultData =
    localStorage.getItem("feedback") &&
    JSON.parse(localStorage.getItem("feedback"));

  const user =
    localStorage.getItem("user") && JSON.parse(localStorage.getItem("user"));

  const {
    register,
    getValues,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm({
    defaultValues: { name: user?.user_name || "", ...defaultData },
  });

  useEffect(() => {
    console.log('Выполнена замена');
    return () => updateValues();
  }, []);

  const updateValues = () => {
    if (JSON.stringify(defaultData) !== JSON.stringify(getValues())) {
      console.log('Замена данных на', JSON.stringify(getValues()))
      const data = JSON.stringify(getValues())
      localStorage.setItem("feedback", data);
    }
  };


  const onSubmit = (data) => {
    // setFeedbackForm(data);
    // console.log("Форма",feedbackForm)

    reset({
      name: "",
      phone: "",
      description: "",
    });
    localStorage.removeItem("feedback");
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <TextInput
        label={"Имя"}
        errors={errors}
        name={"name"}
        register={register}
        validate={{ required: true }}
      />
      <TextInput
        label={"Телефон"}
        errors={errors}
        name={"phone"}
        register={register}
        validate={{ required: true }}
      />
      <TextInput
        label={"Обратная связь"}
        errors={errors}
        name={"description"}
        register={register}
        validate={{ required: true }}
      />
      <button>Отправить данные</button>
    </form>
  );
};
