import React, {useEffect, useState} from "react";
import { CreateProduct } from "./products_component/product_create/product_create";
import { getProducts } from "../../request/list_request";
import { FoodCard } from "./products_component/product_card/product_card";
import { removeOneProduct } from "../../request/list_request";
import { EditProduct } from "./products_component/product_edit/product_edit";

export const ProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const [isEdit, setIsEdit] = useState({status: false, id: null});

    const addProduct = () => {
        setIsModalOpen(true);
    };

    useEffect(() =>{
        getProducts()
            .then(({data}) => {
                setProducts(data.map((elem) => ({...elem, cost: 10})));
            })
            .catch(() => null);
    }, []);


    const removeProduct = (id) => {
        removeOneProduct(id)
          .then(({}) => {
            setProducts((prevValue) =>
              prevValue.filter((product) => product.id !== id)
            );
          })
          .catch(() => alert("Ошибка"));
      };
    
      const onCloseModal = () => {
        setIsModalOpen(false);
      };

    return (
        <div className="productPage">
            <button className="productPage__add-button" onClick={addProduct}>add product</button>
            <span>{products.length}</span>
           
            <div>
                {products.map( (product) => {
                    <CardProduct
                        {...product}
                        key={product.is}
                        name={product.title}
                        price={product.cost}
                        onRemove={removeProduct}
                        onEdit={setIsEdit}
                    />
                })} 
            </div>
        </div>
    );
}
