import React, {useEffect, useState} from "react";
import { CreateProduct } from "./products_component/product_create/product_create";
import { getProducts } from "../../request/list_request";
import { FoodCard } from "./products_component/product_card/product_card";
import { removeOneProduct } from "../../request/list_request";
import { EditProduct } from "./products_component/product_edit/product_edit";
import { set } from "react-hook-form";

export const ProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const [isEdit, setIsEdit] = useState({status: false, id: null});

    const addProduct = () => {
        setIsModalOpen(true);
    };

    useEffect(() =>{
        getProducts()
            .then(({data}) => {
                setProducts(data.map((elem) => ({...elem, cost: 10})));
            })
            .catch(() => null);
    }, []);


    const removeProduct = (id) => {
        removeOneProduct(id)
          .then(({}) => {
            setProducts((prevValue) =>
              prevValue.filter((product) => product.id !== id)
            );
          })
          .catch(() => alert("Ошибка"));
      };
    
      const onCloseModal = () => {
        setIsModalOpen(false);
      };

    return (
        <div className="productPage">

            <h1 className="productPage__head">All Our <span className="productPage__head-part">Products</span></h1>
            <button className="productPage__add-button" onClick={addProduct}>add product</button>
            <span>{products.length}</span>
           
            <div style={{width: "100%", height: "auto", display: "flex"}}>
                {products.map( (product) => {
                    return(
                        <FoodCard
                            {...product}
                            key={product.is}
                            name={product.title}
                            price={product.cost}
                            image={product.img}
                            onRemove={removeProduct}
                            onEdit={setIsEdit}
                        />
                    )
                })} 
            </div>

            <CreateProduct
                setProducts={setProducts}
                onCloseModal={onCloseModal}
                isModalOpen={isModalOpen}
            />

            {isEdit.status && (
                <EditProduct
                    setProducts={setProducts}
                    onCloseModal={setIsEdit}
                    isModalOpen={isEdit.status}
                    initialValues={
                        products.filter((product) => product.id === isEdit.id)[0]
                    }
                    setIsEdit={setIsEdit}
                    id={isEdit.id}
                />
            )}
        </div>
    );
};
