import React from "react";
import { useForm } from "react-hook-form";
import { TextInput } from "../../components/inputs_components/text-input/text-input";
export const AuthPage = () => {
    
    const navigate = useNavigate();

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const onSubmit = (data) => {
        getAuth().then(({ data }) => {
        navigate("/");
        setIsAuth(true);
        localStorage.setItem("token", data.token);
        });
    };
    
    return(  
        <div className="authPage">
        <h1 className="authPage__head">Join Us and <span className="authPage__head-part">Enjoy</span></h1>
        <form onSubmit={handleSubmit(onSubmit)} className="authPage__form-wrap">
            <TextInput
                errors={errors}
                name={"name"}
                label={"Имя"}
                auth={auth}
                validate={{required: true}}
                placeholder="Введите имя"
                type="text"
            />
            <TextInput
                errors={errors}
                name={"phone"}
                label={"Телефон"}
                auth={auth}
                validate={{required: true}}
                placeholder="Введите телефон начиная с +"
                type="text"
            />
            <TextInput
                errors={errors}
                name={"password"}
                label={"Пароль"}
                auth={auth}
                validate={{required: true}}
                placeholder="Введите пароль"
                type="password"
            />
            <button className="authPage__button">Sign Up</button>
        </form>
    </div>
    );
};
